import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatDialogModule } from '@angular/material/dialog';

@Component({
    selector: 'app-base',
    standalone: true,
    imports: [CommonModule, MatDialogModule],
    template: `<h2 mat-dialog-title>Install Angular</h2>
        <mat-dialog-content class="mat-typography">
            <h3>Develop across all platforms</h3>
            <p>
                Learn one way to build applications with Angular and reuse your code and abilities to build apps for any
                deployment target. For web, mobile web, native mobile and native desktop.
            </p>
        </mat-dialog-content>
        <mat-dialog-actions align="end">
            <button mat-button mat-dialog-close>Cancel</button>
            <button mat-button [mat-dialog-close]="true" cdkFocusInitial>Save</button>
        </mat-dialog-actions> `,
    styleUrls: [],
})
export class BaseComponent {
    public save() {}

    public close() {}
}
